import React from "react";
import { useGlobalContext } from "../context";

export default function Meal(){
    const { meals } = useGlobalContext()
    console.log(meals)
   
    return (
        <section className="cards container"> 
            { meals.map((me)=>{
            return (
              
                    <div className="row ">
                      <div className="col-4"   >
                <div key={me.idMeal} className='card mt-4'>
                 <img scr={me.strMealThumb} alt='img1'/>

                 <h4 className="card-title">{ me.strMeal}</h4>
                 </div>
                 </div>
                 </div>
                
            )
        })}
       </section> 

    )
}
