<section className="container-fluid bg-dark d-flex justify-content-center text-white text-center"> 

 <h6>Favorites</h6>
 <div className="container-fluid bg-dark d-inline-flex justify-content-start p-2 ">
{ favmeals.map((me)=>{
            return (
                <div key={me.idMeal} >
             
               
                 <img src={me.strMealThumb} alt="meal im" className="img-fluid fav-img rounded-circle p-2 "/>
               
                 <p>remove</p> 
                
                
                
                
                 </div> 
                
            )
        })}
          </div>
       </section> 
        
    )
}
