import React, {useContext, useEffect, useState} from "react"


const allMealsURL= "https://www.themealdb.com/api/json/v1/1/search.php?s=";
const random = "https://www.themealdb.com/api/json/v1/1/random.php";
const AppContext = React.createContext()

const AppProvider = ({children}) =>{
  const [meals,setMeals] = useState([]);
  const [loading,setLoading] = useState(false);
  const [searchTerm,setSearchTerm] = useState('');
  const [showModal,setShowModal] = useState(true);
  const fetchData = async (url) => {
    setLoading(true)
    try{
    const response = await fetch(url)
    const data= await response.json(response);
    setMeals(data.meals)}
    catch(err){
      console.log('error',err)
      setMeals([])
    }
   setLoading(false)
  }
  const fetchRandomMeal = (url)=>{
    fetchData(random)
  }
  
  useEffect(()=> {
        fetchData(`${allMealsURL}${searchTerm}`);
    }
       ,[searchTerm]);

return(
 <AppContext.Provider value={{loading,meals,setSearchTerm,fetchRandomMeal,showModal,setShowModal}}>
  {children}

</AppContext.Provider>)
}

const useGlobalContext = () => {
  return useContext(AppContext)
}
export {AppContext,AppProvider,useGlobalContext}
