import React from "react";
import { useGlobalContext } from "../context";

export default function Meal(){
    const { meals } = useGlobalContext()
    console.log(meals)
   
    return (
        <section> 
            { meals.map((me)=>{
            return (
                <div className="container">
                    <div className="row">
                      <div className="col-4"   >
                <div key={me.idMeal} className='card mt-4'>
                 <img scr={me.strMealThumb} alt='img1'/>

                 <h4>{ me.strMeal}</h4>
                 </div>
                 </div>
                 </div>
                </div>
                
            )
        })}
       </section> 

    )
}
