import React ,{useState} from "react";
import { useGlobalContext } from "../context";


export default function Search(){
    const { setSearchTerm } = useGlobalContext()
    const [searchValue,setSearchValue] = useState('')
    const handleChange = (event)=> {
       
        setSearchValue(event.target.value)
       }
    const handleSubmit = (e )=> 
    { setSearchTerm (searchValue)
        setSearchValue('')
       e.preventDefault()
    }  
    return (
        <header>
           <form onSubmit={handleSubmit}>
            <div className="search-container p-2 d-inline-flex flex-row">
            <input type="text" className="form-control-sm mx-2" placeholder ="Search a Meal" onChange={handleChange} value={searchValue}/> 
            <button type="submit" className="btn btn-primary mx-2" >search</button>
            <button type="submit" className="btn btn-success mx-2">Surprise Me !</button>
            </div>
            </form> 
        </header>
    )
}
