import React from "react";
import { useGlobalContext } from "../context";

export default function Meal(){
    const { meals } = useGlobalContext()
    console.log(meals)
   
    return (
        <section className="cards container-fluid"> 
            { meals.map((me)=>{
            return (
                <div className="row ustify-content-md-center">
                 <div className="col col-md-4 col-lg-4" > 
                <div key={me.idMeal} className='card m-5 shadow rounded bg-light '>
                 <img src={me.strMealThumb} alt="meal im" className="img-fluid rounded max-auto d-block"/>
                <div className='p-2 text-center'>
                <p><span>{ me.strMeal}</span></p> 
                 <button>click Me</button>
                 </div>
                 </div>
                 </div>
                 </div> 
                
            )
        })}
       </section> 

    )
}
